import React, { Component } from "react";
import classNames from "classnames";
import { Card as Base, CardHeader, CardContent } from "@material-ui/core";
import PropTypes from "prop-types";
import styles from "./ListCard.module.scss";

class ListCard extends Component {
  render() {
    const { theme, cardHeader, cardItems } = this.props;

    const themeObj = {};

    switch (theme) {
      case "light":
        themeObj.class = "light";
        break;
      case "dark":
        themeObj.class = "dark";
        break;
      default:
        themeObj.class = "light";
    }

    const classes = classNames("ui-button", className, {
      [themeObj.class]: [theme]
    });

    return (
      <Base classes={{ root: styles.theme }}>
        <CardHeader classes={{ root: styles.headerSpace }} title={cardHeader} />
        <CardContent classes={{ root: styles.itemSpace }} component="ul">
          {cardItems.map(item => (
            <li className={styles.listItem} key={item.key}>
              -{item}
            </li>
          ))}
        </CardContent>
      </Base>
    );
  }
}

ListCard.propTypes = {
  cardHeader: PropTypes.string,
  cardItems: PropTypes.arrayOf(PropTypes.string)
};

ListCard.defaultProps = {};

export default ListCard;
