import React, { Component } from "react";
import { Card as Base, CardHeader, CardContent } from "@material-ui/core";
import PropTypes from "prop-types";
import styles from "./ListCard.module.scss";

class ListCard extends Component {
  render() {
    const { cardHeader, cardItems } = this.props;

    return (
      <Base>
        <CardHeader classes={{ root: styles.headerSpace }} title={cardHeader} />
        <CardContent>
          <ItemList className={styles.listItem} items={cardItems} />
        </CardContent>
      </Base>
    );
  }
}

ListCard.propTypes = {
  cardHeader: PropTypes.string,
  cardItems: PropTypes.arrayOf(PropTypes.string)
};

ListCard.defaultProps = {};

function ItemList({ className, items }) {
  return items ? (
    <ul>
      {items.map(item => (
        <li className={className} key={item.key}>
          {item}
        </li>
      ))}
    </ul>
  ) : null;
}

export default ListCard;
